package com.demo.camel;import org.apache.camel.builder.RouteBuilder;import org.apache.camel.model.rest.RestBindingMode;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class MainRouteBuilder extends RouteBuilder {	private static final Logger LOGGER = LoggerFactory			.getLogger(MainRouteBuilder.class);	@Override	public void configure() throws Exception {		restConfiguration().component("jetty").host("0.0.0.0")				.port(80)				.bindingMode(RestBindingMode.auto)				.dataFormatProperty("prettyPrint", "true")				.apiContextPath("/api-doc")				.apiProperty("api.title", "User API")				.apiProperty("api.version", "1.2.3")				.apiProperty("cors", "true");	    from("twitter://streaming/filter?type=polling&amp;keywords=history&amp;delay=6000&amp;accessToken={{access.token}}&amp;accessTokenSecret={{access.token-secret}}&amp;consumerKey={{consumer.key}}&amp;consumerSecret={{consumer.secret}}")	      .to("file:/tmp/c2k/output/");	}}